import {useMediaQuery} from "react-responsive";
import React from "react";

export function useListPageCssSizeProps() {
    const isMobile = useMediaQuery({maxWidth: 414})

    let cssProps: React.CSSProperties = {
        width: '50vw',
        minWidth: '600px',
        maxWidth: '1200px',
        height: '100vh',
    }

    if (isMobile) {
        cssProps = {
            width: '100vh',
            paddingLeft: '16px',
            paddingRight: '16px',
            height: '100vh',
        }
    }

    return { cssProps }
}
