import {PostCard} from "./post-card";
import {LoadMoreCard} from "../list-page/load-more-card";
import React, {useCallback, useEffect} from "react";
import {PostItem} from "../+requests";

interface Props{
    list: PostItem[]
    isLoadingMorePost?: boolean;
    onScrollToEnd: () => void;
}
export function PostListView({
    list,
    isLoadingMorePost,
    onScrollToEnd,
}: Props){

    const getListIfScrollToEnd = useCallback(() => {
        if (!document.scrollingElement?.scrollHeight) {
            return;
        }

        if (document.documentElement.scrollTop + window.innerHeight < document.scrollingElement.scrollHeight) {
            return;
        }

        onScrollToEnd()
    }, [onScrollToEnd]);

    useEffect(() => {
        window.addEventListener('scroll', getListIfScrollToEnd)

        return () => {
            window.removeEventListener('scroll', getListIfScrollToEnd)
        }
    }, [getListIfScrollToEnd])

    return (
        <div style={{display: 'flex', flexDirection: 'column', gap: '20px'}}>
            {list.map((post, index) => (
                <PostCard key={index} post={post}/>
            ))}
            <LoadMoreCard isVisible={isLoadingMorePost}/>
        </div>
    )
}
