import React, {useEffect, useState} from 'react';
import './App.css';
import axios, {AxiosResponse} from "axios";
import moment from "moment";

type CollectionAxiosModel<T> = AxiosResponse<CollectionResponseModel<T>>

interface CollectionResponseModel<T>{
    status: number
    success: boolean
    data: T[]
}

interface Post{
    title: string
    link: string
    date: string
}

type PostListResponseModel = CollectionAxiosModel<Post>

function App() {
    const [list, setList] = useState<Post[]>([])
    const [isListError, setIsListError] = useState(false)
    const [isListLoading, setIsListLoading] = useState(true)

    const client = axios.create({
        baseURL: 'http://127.0.0.1:8000/',
    });

    function getDate(post: Post){
        const date = moment(post.date)
        return date.format('MMM Do, YYYY')
    }

    async function getList(){
        setIsListLoading(true)
        setIsListError(false)

        try{
            const res: PostListResponseModel = await client.get('api/posts')
            setList(res.data.data)
        }catch (error){
            setIsListError(true)
        }finally {
            setIsListLoading(false)
        }
    }

    useEffect(() => {
        void getList()
    }, []);

    if (isListError){
        return (
            <div
                style={{
                    backgroundColor: 'white',
                    display: 'flex',
                    justifyContent: 'center',
                    width: '100vw',
                    height: '100vh',
                }}>
                <div
                    style={{
                        marginTop: '16px',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: '50vw',
                        minWidth: '400px',
                        maxWidth: '1200px',
                        height: '100vh',
                    }}>
                    <h1>Awkward...There seems to be a problem. Try refreshing.</h1>
                </div>
            </div>
        )
    }

    if (isListLoading){
        return (
            <div
                style={{
                    backgroundColor: 'white',
                    display: 'flex',
                    justifyContent: 'center',
                    width: '100vw',
                    height: '100vh',
                }}>
                <div
                    style={{
                        marginTop: '16px',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: '50vw',
                        minWidth: '400px',
                        maxWidth: '1200px',
                        height: '100vh',
                    }}>
                    <h1>Loading...</h1>
                </div>
            </div>
        )
    }

    if (list.length === 0){
        return (
            <div
                style={{
                    backgroundColor: 'white',
                    display: 'flex',
                    justifyContent: 'center',
                    width: '100vw',
                    height: '100vh',
                }}>
                <div
                    style={{
                        marginTop: '16px',
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'flex-start',
                        gap: '16px',
                        width: '50vw',
                        minWidth: '400px',
                        maxWidth: '1200px',
                        height: '100vh',
                    }}>
                    <h2>Mashable Posts</h2>
                    <p>There are no posts from Mashable.</p>
                </div>
            </div>
        )
    }

    return (
        <div
            style={{
                backgroundColor: 'white',
                display: 'flex',
                justifyContent: 'center',
                width: '100vw',
                height: '100vh',
            }}>
            <div
                style={{
                    marginTop: '16px',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    gap: '16px',
                    width: '50vw',
                    minWidth: '400px',
                    maxWidth: '1200px',
                    height: '100vh',
                }}>
                <h2>Mashable Posts</h2>
                {list.map((post, index) => (
                    <div
                        key={index}
                        style={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: '4px',
                            alignItems: 'flex-start'
                        }}>
                        <a href={post.link}>{post.title}</a>
                        <p>{getDate(post)}</p>
                    </div>
                ))}
            </div>
        </div>
    )
}

export default App;
