import {useMediaQuery} from "react-responsive";
import React from "react";

export function useListPageCssSizeProps() {
    const isMobileOrTablet = useMediaQuery({maxWidth: 983})

    let cssProps: React.CSSProperties = {
        width: '50vw',
        minWidth: '983',
        maxWidth: '1200px',
        height: '100vh',
    }

    if (isMobileOrTablet) {
        cssProps = {
            width: '100vh',
            paddingLeft: '16px',
            paddingRight: '16px',
            height: '100vh',
        }
    }

    return { cssProps }
}
