import React, {useCallback, useEffect, useMemo, useState} from 'react';
import './App.css';
import axios, {AxiosResponse, CanceledError, GenericAbortSignal} from "axios";
import moment from "moment";
import {ErrorPage} from "./error-page";
import {LoadingPage} from "./loading-page";
import {ListPage} from "./list-page/list-page";
import {usePostsRetriever} from "./+utils/use-posts-retriever";

export type CollectionAxiosModel<T> = AxiosResponse<CollectionResponseModel<T>>

export interface CollectionResponseModel<T> {
    status: number
    success: boolean
    data: T[]
}

export interface Post {
    title: string
    link: string
    date: string
}

export type PostListResponseModel = CollectionAxiosModel<Post>

function App() {
    const [page, setPage] = useState(1)

    const {
        isListLoading,
        isListError,
        list,
        isListInitialized,
    } = usePostsRetriever({ page })

    if (isListError) {
        return <ErrorPage/>
    }

    if (!isListInitialized) {
        return <LoadingPage/>
    }

    return (
        <ListPage
            list={list}
            isListLoading={isListLoading}
            onScrollToEnd={() => setPage(page => page + 1)}/>
    )
}

export default App;
