import React, {useCallback, useEffect, useState} from 'react';
import './App.css';
import axios, {AxiosResponse} from "axios";
import moment from "moment";

type CollectionAxiosModel<T> = AxiosResponse<CollectionResponseModel<T>>

interface CollectionResponseModel<T> {
    status: number
    success: boolean
    data: T[]
}

interface Post {
    title: string
    link: string
    date: string
}

type PostListResponseModel = CollectionAxiosModel<Post>

function App() {
    const [list, setList] = useState<Post[]>([])
    const [isListError, setIsListError] = useState(false)
    const [isListLoading, setIsListLoading] = useState(true)
    const [isInitializing, setIsInitializing] = useState(true)
    const [page, setPage] = useState(1)
    const [isLastPage, setIsLastPage] = useState(false)

    const client = axios.create({
        baseURL: 'http://127.0.0.1:8000/',
    });

    function getDate(post: Post) {
        const date = moment(post.date)
        return date.format('MMM Do, YYYY')
    }

    const getList = useCallback(async () => {
        if (isLastPage){
            return;
        }

        setIsListLoading(true)
        setIsListError(false)

        try {
            const res: PostListResponseModel = await client.get(`api/posts?page=${page}`)
            const newList = res.data.data

            if (newList.length === 0){
                setIsLastPage(true)
            }

            setList(list => [...list, ...newList])
        } catch (error) {
            setIsListError(true)
        } finally {
            setIsListLoading(false)
            setIsInitializing(false)
        }
    }, [client, isLastPage, page]);

    const getListIfScrollToEnd = useCallback(() =>{
        if (window.innerHeight + document.documentElement.scrollTop !== document.documentElement.offsetHeight){
            return;
        }

        if (isInitializing || isListLoading || isListError){
            return;
        }

        setPage(page => page + 1)
    }, [isInitializing, isListError, isListLoading]);

    useEffect(() => {
        window.addEventListener('scroll', getListIfScrollToEnd)
        return () => window.removeEventListener('scroll', getListIfScrollToEnd)
    }, [getListIfScrollToEnd])

    useEffect(() => {
        void getList()
    }, [getList]);

    if (isListError) {
        return (
            <div
                style={{
                    backgroundColor: 'white',
                    display: 'flex',
                    justifyContent: 'center',
                    width: '100vw',
                    height: '100vh',
                }}>
                <div
                    style={{
                        marginTop: '16px',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: '50vw',
                        minWidth: '400px',
                        maxWidth: '1200px',
                        height: '100vh',
                    }}>
                    <h1>Awkward...There seems to be a problem. Try refreshing.</h1>
                </div>
            </div>
        )
    }

    if (isInitializing) {
        return (
            <div
                style={{
                    backgroundColor: 'white',
                    display: 'flex',
                    justifyContent: 'center',
                    width: '100vw',
                    height: '100vh',
                }}>
                <div
                    style={{
                        marginTop: '16px',
                        display: 'flex',
                        flexDirection: 'column',
                        justifyContent: 'center',
                        alignItems: 'center',
                        width: '50vw',
                        minWidth: '400px',
                        maxWidth: '1200px',
                        height: '100vh',
                    }}>
                    <h1>Loading...</h1>
                </div>
            </div>
        )
    }

    if (list.length === 0) {
        return (
            <div
                style={{
                    backgroundColor: 'white',
                    display: 'flex',
                    justifyContent: 'center',
                    width: '100vw',
                    height: '100vh',
                }}>
                <div
                    style={{
                        marginTop: '16px',
                        display: 'flex',
                        flexDirection: 'column',
                        alignItems: 'flex-start',
                        gap: '16px',
                        width: '50vw',
                        minWidth: '400px',
                        maxWidth: '1200px',
                        height: '100vh',
                    }}>
                    <h2>Mashable Posts</h2>
                    <p>There are no posts from Mashable.</p>
                </div>
            </div>
        )
    }

    return (
        <div
            style={{
                backgroundColor: 'white',
                display: 'flex',
                justifyContent: 'center',
                width: '100vw',
                height: '100vh',
            }}>
            <div
                style={{
                    marginTop: '16px',
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: 'flex-start',
                    gap: '16px',
                    width: '50vw',
                    minWidth: '400px',
                    maxWidth: '1200px',
                    height: '100vh',
                }}>
                <h2>Mashable Posts</h2>
                {list.map((post, index) => (
                    <div
                        key={index}
                        style={{
                            display: 'flex',
                            flexDirection: 'column',
                            gap: '4px',
                            alignItems: 'flex-start'
                        }}>
                        <a href={post.link}>{post.title}</a>
                        <p>{getDate(post)}</p>
                    </div>
                ))}
                <div
                    style={{
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        padding: '16px',
                    }}>
                    <p
                        style={{
                            color: isListLoading ? 'white' : 'unset',
                        }}>
                        Loading more posts...
                    </p>
                </div>
            </div>
        </div>
    )
}

export default App;
